<script setup lang="ts">
import { ref, computed } from 'vue'

interface NavItem {
  id: string
  label: string
  href?: string
  isActive?: boolean
}

interface Props {
  navItems?: NavItem[]
  showSearch?: boolean
  showCurrency?: boolean
  showCart?: boolean
  showAuth?: boolean
  cartCount?: number
  isLoggedIn?: boolean
  username?: string
}

const props = withDefaults(defineProps<Props>(), {
  navItems: () => [
    { id: 'company', label: 'Company', href: '#company' },
    { id: 'catalogue', label: 'Catalogue', href: '#catalogue' },
    { id: 'stores', label: 'Stores', href: '#stores' }
  ],
  showSearch: true,
  showCurrency: true,
  showCart: true,
  showAuth: true,
  cartCount: 0,
  isLoggedIn: false,
  username: ''
})

const emit = defineEmits<{
  navigate: [item: NavItem]
  search: [query: string]
  currencyChange: [currency: string]
  cartClick: []
  authClick: []
}>()

const activeNavItem = ref<string>('')
const searchQuery = ref('')
const selectedCurrency = ref('USD')

const cartDisplay = computed(() => {
  return props.cartCount > 0 ? `Cart (${props.cartCount})` : 'Cart'
})

const authDisplay = computed(() => {
  return props.isLoggedIn ? props.username || 'Profile' : 'Log in'
})

const handleNavClick = (item: NavItem) => {
  activeNavItem.value = item.id
  emit('navigate', item)

  if (item.href) {
    window.location.href = item.href
  }
}

const handleSearch = () => {
  emit('search', searchQuery.value)
}

const handleCurrencyChange = (currency: string) => {
  selectedCurrency.value = currency
  emit('currencyChange', currency)
}

const handleCartClick = () => {
  emit('cartClick')
}

const handleAuthClick = () => {
  emit('authClick')
}
</script>

